import React, { useState } from "react";
import {
  makeStyles,
  styled,
  Card,
  CardActionArea,
  CardMedia,
  CardHeader,
  Avatar,
  IconButton,
  CardContent,
  Typography,
  Collapse,
  Modal,
  Container,
  Button,
} from "@material-ui/core";

import { Close, MoreVert as MoreVertIcon } from "@material-ui/icons";
import { } from "react-router-dom";
// import Food from "/images/food.jpg";
import { red } from "@material-ui/core/colors";


const ExpandMore = styled((props) => {
    const { expand, ...other } = props;
    return <IconButton {...other} />;
  })(({ theme, expand }) => ({
    // transform: !expand ? 'rotate(0deg)' : 'rotate(180deg)',
    marginLeft: 'auto',
  }));


const useStyles = makeStyles((theme) => ({
    cardarea:{
        marginBottom: "20px",
        border: "none",
        boxShadow: "none",
    },
    modalcontainer: {
        width: "100%",
        height: "90vh",
        marginTop: "5vh",
        backgroundColor: "#FFF",
        position: "absolute",
        top:0,
        bottom:0,
        left:0,
        right:0,
        margin: "auto",
        border: "none",
        borderRadius: "0",
        maxWidth: "99%",
    },
    seemore: {
      display: "inline-block",
      color: "#000",
      fontSize: "16px",
      padding: "0",
      '&:hover': {
        background: "none",
        color: "#2CB34A",
    },
    '&:focus': {
        color: "#2CB34A",
    },
    },
}));

function Post({post}) {
  console.log(post);
    const [expanded, setExpanded] = React.useState(false);

    const handleExpandClick = () => {
      setExpanded(!expanded);
    };

  const classes = useStyles();
  const [open, setOpen] = useState(false);
//   const //theme = useTheme();
//   const isMobile = useMediaQuery(theme.breakpoints.down("sm"));

  return (
      <Card className={classes.cardarea}>
          <CardActionArea>
          <CardHeader
        avatar={
          <Avatar sx={{ bgcolor: red[500] }} aria-label="recipe">
            R
          </Avatar>
        }
        action={
          <IconButton aria-label="settings">
            <MoreVertIcon />
          </IconButton>
        }
        title="Connect Magazine  •  #TheSpiritOfSafaricom"
        subheader={post.date}
      />
          </CardActionArea>

          <CardContent>
        <Typography variant="body2" color="text.secondary" paragraph className="Para">
          {post.dec}
        </Typography>
        <ExpandMore className={classes.seemore}
          expand={expanded}
          onClick={handleExpandClick}
          aria-expanded={expanded}
          aria-label="show more"
        >
          See More
        </ExpandMore>
        <Collapse in={expanded} timeout="auto" unmountOnExit>
        <CardContent>
          <Typography paragraph>Method:</Typography>
          <Typography paragraph>
            Heat 1/2 cup of the broth in a pot until simmering, add saffron and set
            aside for 10 minutes.
          </Typography>
          <Typography paragraph>
            Heat oil in a (14- to 16-inch) paella pan or a large, deep skillet over
            medium-high heat. Add chicken, shrimp and chorizo, and cook, stirring
            occasionally until lightly browned, 6 to 8 minutes. Transfer shrimp to a
            large plate and set aside, leaving chicken and chorizo in the pan. Add
            pimentón, bay leaves, garlic, tomatoes, onion, salt and pepper, and cook,
            stirring often until thickened and fragrant, about 10 minutes. Add
            saffron broth and remaining 4 1/2 cups chicken broth; bring to a boil.
          </Typography>
          <Typography paragraph>
            Add rice and stir very gently to distribute. Top with artichokes and
            peppers, and cook without stirring, until most of the liquid is absorbed,
            15 to 18 minutes. Reduce heat to medium-low, add reserved shrimp and
            mussels, tucking them down into the rice, and cook again without
            stirring, until mussels have opened and rice is just tender, 5 to 7
            minutes more. (Discard any mussels that don’t open.)
          </Typography>
          <Typography>
            Set aside off of the heat to let rest for 10 minutes, and then serve.
          </Typography>
        </CardContent>
      </Collapse>
      </CardContent>

      <CardMedia
        component="img"
        height="194"
        image="/images/food.jpg"
        alt="Paella dish"
        onClick={() => setOpen(true)}
      />

      <Modal open={open}>
          <Container className={classes.modalcontainer}>
          <Button onClick={() => setOpen(false)}><Close /></Button>
          <Typography>Hello World</Typography>
          </Container>
      </Modal>

      </Card>
  );
}
export default Post;
