import React from "react";
import {
  AppBar,
  Toolbar,
  CssBaseline,
  Typography,
  InputBase,
  makeStyles,
  useTheme,
  useMediaQuery,
  alpha,
} from "@material-ui/core";

import { Search } from "@material-ui/icons";
import { Link } from "react-router-dom";
import DrawerComponent from "./Drawer";

import Logo from '../images/safaricom-connect-logo.png'

const useStyles = makeStyles((theme) => ({
    toolbar: {
        display: "flex",
        justifyContent: "space-between",
        backgroundColor: "#162d54",
    },
        navlinks: {
        marginLeft: theme.spacing(5),
        display: "flex",
    },
    logo: {
        flexGrow: "1",
        cursor: "pointer",
    },
    searcharea: {
        display: "flex",
        alignItems: "center",
        justifyContent: "space-between",
        padding: "2px 10px",
        backgroundColor: alpha(theme.palette.common.white, 0.15),
        '&:hover': {
            backgroundColor: alpha(theme.palette.common.white, 0.25),
        },
        marginLeft: theme.spacing(5),
        marginRight: theme.spacing(5),
        borderRadius: theme.shape.borderRadius,
        // width: "20%",
    },
    input: {
        color: 'inherit',
  '& .MuiInputBase-input': {
    padding: theme.spacing(1, 1, 1, 0),
    // vertical padding + font size from searchIcon
    paddingLeft: `calc(1em + ${theme.spacing(4)})`,
    transition: theme.transitions.create('width'),
    width: '100%',
    [theme.breakpoints.up('sm')]: {
      width: '12ch',
      '&:focus': {
        width: '20ch',
      },
    },
  },
    },
    link: {
        textDecoration: "none",
        color: "white",
        fontSize: "16px",
        marginLeft: theme.spacing(5),
        "&:hover": {
            color: "#fdc600",
        borderBottom: "1px solid white",
    },
  },
}));

function Navbar() {
  const classes = useStyles();
  const theme = useTheme();
  const isMobile = useMediaQuery(theme.breakpoints.down("sm"));

  return (
    <AppBar position="static">
      <CssBaseline />
      <Toolbar className={classes.toolbar}>
      <div class="header-logo">
            <Link to="/" className=""><img src={Logo} alt="Safaricom Logo" /></Link>
        </div>

        {isMobile ? (
          <DrawerComponent />
        ) : (
          <div className={classes.navlinks}>
            <Link to="/" className={classes.link}>
              Home
            </Link>
            <Link to="/editions" className={classes.link}>
              Editions
            </Link>
          </div>
        )}

        <div className={classes.searcharea}>
            <InputBase placeholder="Search..." className={classes.input}/>
            <Search />
        </div>


        <div className={classes.iconarea}>Icons</div>
      </Toolbar>
    </AppBar>
  );
}
export default Navbar;
